import React, { useState, useEffect } from 'react';
import Cookies from 'js-cookie';
import Header from '../Header/Header';
import '../routes.css';
import './products.css';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', description: '', price: '', stock: '' });
  const [editMode, setEditMode] = useState(false);
  const [currentProductId, setCurrentProductId] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = Cookies.get('authToken');
        if (!token) {
          console.error('No auth token found!');
          return;
        }

        const response = await fetch('http://localhost:5000/api/products', {
          headers: { Authorization: `Bearer ${token}` },
          credentials: 'include',
        });

        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };
    fetchProducts();
  }, []);

  // Add or Update Product
  const handleSaveProduct = async () => {
    if (!newProduct.name || !newProduct.description || !newProduct.price || !newProduct.stock) {
      alert('All fields must be filled out.');
      return;
    }

    try {
      const token = Cookies.get('authToken');
      const method = editMode ? 'PUT' : 'POST';
      const url = editMode
        ? `http://localhost:5000/api/products/${currentProductId}`
        : 'http://localhost:5000/api/products';

      const response = await fetch(url, {
        method,
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(newProduct),
      });

      const updatedProduct = await response.json();

      if (editMode) {
        setProducts((prev) =>
          prev.map((product) => (product.id === updatedProduct.id ? updatedProduct : product))
        );
      } else {
        setProducts((prev) => [...prev, updatedProduct]);
      }

      // Reset form
      setNewProduct({ name: '', description: '', price: '', stock: '' });
      setEditMode(false);
      setCurrentProductId(null);
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };

  // Delete Product
  const handleDeleteProduct = async (productId) => {
    try {
      const token = Cookies.get('authToken');
      await fetch(`http://localhost:5000/api/products/${productId}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
        credentials: 'include',
      });

      setProducts((prev) => prev.filter((product) => product.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  // Edit Product
  const handleEditProduct = (product) => {
    setNewProduct(product);
    setEditMode(true);
    setCurrentProductId(product.id);
  };

  return (
    <div className="routes-bg">
      <Header />
      <div className="admin-bg">
        <h1>Admin Dashboard</h1>
        <div className="admin-input-containers-list">
          <div className="admin-input-container">
            <label htmlFor="product-name">Name</label>
            <br />
            <input
              type="text"
              id="product-name"
              placeholder="Enter Name"
              value={newProduct.name}
              onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
            />
          </div>
          <div className="admin-input-container">
            <label htmlFor="product-description">Description</label>
            <br />
            <input
              type="text"
              id="product-description"
              placeholder="Enter Description"
              value={newProduct.description}
              onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
            />
          </div>
          <div className="admin-input-container">
            <label htmlFor="product-price">Price</label>
            <br />
            <input
              type="number"
              id="product-price"
              placeholder="Enter Price"
              value={newProduct.price}
              onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
            />
          </div>
          <div className="admin-input-container">
            <label htmlFor="product-stock">Stock</label>
            <br />
            <input
              type="number"
              id="product-stock"
              placeholder="Enter Stock"
              value={newProduct.stock}
              onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
            />
          </div>
        </div>
        <button onClick={handleSaveProduct} className="admin-btn">
          {editMode ? 'Update Product' : 'Add Product'}
        </button>
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              <h3>{product.name}</h3>
              <p>Description: {product.description}</p>
              <p>Price: ${product.price}</p>
              <p>Stock: {product.stock} units</p>
              <button onClick={() => handleEditProduct(product)}>Edit</button>
              <button onClick={() => handleDeleteProduct(product.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminDashboard;
