import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie'; // Import js-cookie

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', description: '', price: '', stock: '' });

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Retrieve token from cookies
        const token = Cookies.get('authToken');

        // Ensure token is present
        if (!token) {
          console.error('No auth token found!');
          // Handle no token scenario, e.g., redirect to login or show an error
          return;
        }

        // Fetch products with the token
        const result = await axios.get('http://localhost:5000/api/products', {
          headers: { Authorization: `Bearer ${token}` }, // Set Authorization header
          withCredentials: true, // Include credentials like cookies
        });

        setProducts(result.data);
      } catch (error) {
        console.error('Error fetching products:', error);
        // Handle error scenario
      }
    };

    fetchProducts();
  }, []);

  const handleAddProduct = async () => {
    try {
      // Retrieve token from cookies
      const token = Cookies.get('authToken');

      // Ensure token is present
      if (!token) {
        console.error('No auth token found!');
        // Handle no token scenario, e.g., redirect to login or show an error
        return;
      }

      // Add product with the token
      const result = await axios.post('http://localhost:5000/api/products', newProduct, {
        headers: { Authorization: `Bearer ${token}` }, // Set Authorization header
        withCredentials: true, // Include credentials like cookies
      });

      setProducts([...products, result.data]);
      setNewProduct({ name: '', description: '', price: '', stock: '' });
    } catch (error) {
      console.error('Error adding product:', error);
      // Handle error scenario
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <input
        type="text"
        placeholder="Name"
        value={newProduct.name}
        onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Description"
        value={newProduct.description}
        onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
      />
      <input
        type="text"
        placeholder="Price"
        value={newProduct.price}
        onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
      />
      <input
        type="text"
        placeholder="Stock"
        value={newProduct.stock}
        onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
      />
      <button onClick={handleAddProduct}>Add Product</button>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            {product.name} - ${product.price}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
