import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie'; // Ensure js-cookie is imported
import Header from '../Header/Header';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);

  // Assuming you have the userId stored in cookies or you can get it from some global state
  const userId = Cookies.get('userId') || 1; // Replace with actual method to get userId

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const token = Cookies.get('authToken');
        if (!token) {
          console.error('No auth token found!');
          return;
        }

        const result = await axios.get(`/api/cart/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
          withCredentials: true,
        });
        setCartItems(result.data);
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };
    fetchCartItems();
  }, [userId]);

  return (
    <>
      <Header />
      <div>
        <h1>Shopping Cart</h1>
        <ul>
          {cartItems.map(item => (
            <li key={item.id}>
              Product ID: {item.product_id} - Quantity: {item.quantity}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default Cart;
