import React, { useState } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:5000/api/auth/login',
        { username, password },
        { withCredentials: true }
      );
      const { token, role } = response.data;
      
      // Store the token and role in cookies
      Cookies.set('authToken', token, {
        expires: 1, // The cookie will expire in 1 day
        path: '/', // Make the cookie accessible across the site
      });

      Cookies.set('userRole', role, {
        expires: 1, // The cookie will expire in 1 day
        path: '/', // Make the cookie accessible across the site
      });

      // Navigate to the home page after successful login
      navigate('/');
    } catch (error) {
      console.error('Error logging in:', error);
      // Show error message to the user
    }
  };

  return (
    <div className="credentails-bg">
      <form onSubmit={handleSubmit} className="credentials-form">
        <h1 className="credentials-heading">
          Log<span>I</span>n
        </h1>
        <div className="input-container">
          <input
            type="text"
            className="input-credentials"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="input-container">
          <input
            type="password"
            className="input-credentials"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="btn-container">
          <button type="submit" className="register-btn">
            Login
          </button>
        </div>
      </form>
    </div>
  );
};

export default Login;
