import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import Cookies from 'js-cookie';

const AdminRoute = () => {
  const token = Cookies.get('authToken');
  const role = Cookies.get('userRole');

  if (!token || role !== 'admin') {
    return <Navigate to="/" />;
  }

  return <Outlet />;
};

export default AdminRoute;
