import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:5000/api/auth/login', 
        { username, password },
        { withCredentials: true } // Allows sending cookies
      );
      localStorage.setItem('token', response.data.token);
      // Optionally set up Axios default headers for subsequent requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${response.data.token}`;
      // Redirect or show success message
    } catch (error) {
      console.error("Error logging in:", error);
      // Show error message
    }
  };

  return (
    <div className='credentails-bg'>
      <form onSubmit={handleSubmit} className='credentials-form'>
        <h1 className='credentials-heading'>Regist<span>E</span>r</h1>
        <div className='input-container'>
          <input type="text" className='input-credentials' placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div className='input-container'>
          <input type="password" className='input-credentials' placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div className='btn-container'>
          <button type="submit" className='register-btn'>Login</button>
        </div>
      </form>
    </div>
  );
};

export default Login;
